import React, { useReducer } from "react";

import CartContext from "./cart-context";

const DUMMY_ITEMS = [{ id: "c1", name: "Sushi", amount: 2, price: 12.99 }];

const cartReducer = (state, action) => {
  switch (action.type) {
    case "ADD_ITEM":
      const updatedItems = state.concat(action.item);
      return {
        items: updatedItems,
      };
    case "REMOVE_ITEM":
      break;
    default:
  }
};

const CartProvider = (props) => {
  const [items, dispatchItems] = useReducer(cartReducer, DUMMY_ITEMS);

  const addItemToCartHandler = (item) => {
    dispatchItems({ type: "ADD_ITEM", item: item });
  };

  const removeItemFromCartHandler = (id) => {
    dispatchItems({ type: "REMOVE_ITEM", id: id });
  };

  const cartContext = {
    items: items,
    totalAmount: items
      .map((item) => item.price)
      .reduce((prev, next) => prev + next),
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
    onClick: props.onClick,
    onDismiss: props.onDismiss,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
