import React from 'react';

const CartContext = React.createContext({
    items: [],
    totalAmount: 0,
    cartVisible: null,
    addItem: (item) => {},
    removeItem: (id) => {}, 
    onClick: () => {},
    onDismiss: () => {},
});

export default CartContext;
